import React, { useState } from 'react';
import Modal from 'react-modal';
import Button from '../components/Button';
import DeathKnight from '../img/Death_Knight_icon.png';
import DemonHunter from '../img/Demon_Hunter_icon.png';
import Druid from '../img/Druid_icon.png';
import Hunter from '../img/Hunter_icon.png';
import Mage from '../img/Mage_icon.png';
import Paladin from '../img/Paladin_icon.png';
import Priest from '../img/Priest_icon.png';
import Shaman from '../img/Shaman_icon.png';
import Warlock from '../img/Warlock_icon.png';
import Warrior from '../img/Warrior_icon.png';

import '../styles/DeckBuilderPage.css';

const DeckBuilderPage = () => {
    const [modalIsOpen, setModalIsOpen] = useState(false);

    const openModal = () => {
        setModalIsOpen(true);
    };
    const closeModal = () => {
        setModalIsOpen(false);
    };

    return(
        <div className="deckBuilder">
            <p>Decks:</p>
            <Button text='Create New Deck' onClick={openModal} />
            <Modal
                isOpen={modalIsOpen}
                className="classModal"
            >
                <h4>Pick a Class</h4>
                <div className='class'>
                    <img src={DeathKnight} />
                    <p>Death Knight</p>
                </div>
                <div className='class'>
                    <img src={DemonHunter} />
                    <p>Demon Hunter</p>
                </div>
                <div className='class'>
                    <img src={Druid} />
                    <p>Druid</p>
                </div>
                <div className='class'>
                    <img src={Hunter} />
                    <p>Hunter</p>
                </div>
                <div className='class'>
                    <img src={Mage} />
                    <p>Mage</p>
                </div>
                <div className='class'>
                    <img src={Paladin} />
                    <p>Paladin</p>
                </div>
                <div className='class'>
                    <img src={Priest} />
                </div>
                <div className='class'>
                    <img src={Shaman} />
                </div>
                <div className='class'>
                    <img src={Warlock} />
                </div>
                <div className='class'>
                    <img src={Warrior} />
                </div>

                <Button text='Cancel' onClick={closeModal} />
            </Modal>
        </div>
    );
};

export default DeckBuilderPage;
